// display a list of cards with To-Do events input by user
import { useState, useEffect } from "react";
import Card from "./Card";
import Box from "@mui/material/Box";
import Filters from "./Filters";

const CardList = ({ pendingTasks, setPendingTasks }) => {
  // console.log(pendingTasks);
  const [tasks, setTasks] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const [done, setDone] = useState(false);
  const [pending, setPending] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/tasks")
      .then((res) => {
        if (!res.ok) {
          throw Error("Could not fetch the data for that resource");
        }
        return res.json();
      })
      .then((data) => {
        setTasks(data);
        setIsLoading(false);
        setError(null);
        const updatePendingTasks = data.filter((task) => task.status === false);
        // console.log(pendingTasks.length);
        setPendingTasks(updatePendingTasks.length);
      })
      .catch((err) => {
        setIsLoading(false);
        setError(err.message);
      });
  }); // Is tasks correct here?
  // Set the status of the task to false when the task is clicked
  const handleDoneClick = () => {
    setDone(!done);
    setPending(false);
  };

  const handlePendingClick = () => {
    setPending(!pending);
    setDone(false);
  };

  const [allClick, setAllClick] = useState(false);

  const handleAllClick = () => {
    // setDone(false);
    // setPending(false);
    setAllClick(!allClick);

    // Update the status of all tasks
    fetch("http://localhost:8000/tasks", {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
    })
        .then((response) => {
            if (!response.ok) {
                throw new Error("Failed to update task");
            }
            return response.json();
        })
        .then(() => {
            console.log("Task updated");
            setPending(false);
            setTasks(tasks.map((task) => ({ ...task, status: !allClick })));
        })
        .catch((error) => {
            console.error("Error updating task:", error);
            setPending(false);
        });
    };  

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="card-list">
      <Filters
        handleDoneClick={handleDoneClick}
        handlePendingClick={handlePendingClick}
        handleAllClick={handleAllClick}
      />

      <Box
        sx={{
          p: 1,
          borderRadius: 2,
          display: "grid",
          gridTemplateColumns: { md: "1fr" },
          gap: 2,
          alignItems: "center", // Align items in the middle
          padding: "5px",
          marginLeft: "180px",
          marginRight: "180px",
          marginTop: "30px",
        }}
      >
        {!done &&
          !pending &&
          tasks &&
          tasks.map((task) => (
            <Card id={task.id} task={task.task} status={task.status} />
          ))}
        {done &&
          tasks &&
          tasks
            .filter((task) => task.status === true)
            .map((task) => (
              <Card id={task.id} task={task.task} status={task.status} />
            ))}
        {pending &&
          tasks &&
          tasks
            .filter((task) => task.status === false)
            .map((task) => (
              <Card id={task.id} task={task.task} status={task.status} />
            ))}
      </Box>
    </div>
  );
};

export default CardList;
