import * as React from "react";
import Paper from "@mui/material/Paper";
import { createTheme, ThemeProvider, styled } from "@mui/material/styles";
import { Checkbox } from "@mui/material";
import Fab from "@mui/material/Fab";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  textAlign: "center",
  color: theme.palette.text.secondary,
  height: 60,
  lineHeight: "60px",
}));

const darkTheme = createTheme({ palette: { mode: "dark" } });
const lightTheme = createTheme({ palette: { mode: "light" } });

export default function Card({ id, task, initialStatus }) {
    console.log()
  const [status, setStatus] = React.useState(initialStatus);
  const [isPending, setIsPending] = React.useState(false);

  const handleCheckboxClick = () => {
    setIsPending(true);
    const updatedStatus = !status;

    fetch(`http://localhost:8000/tasks/${id}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ status: updatedStatus }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to update task status");
        }
        return response.json();
      })
      .then(() => {
        console.log("Task status updated");
        setStatus(updatedStatus);
        setIsPending(false);
      })
      .catch((error) => {
        console.error("Error updating task status:", error);
        setIsPending(false);
      });
  };

  const handleDeleteClick = () => {
    setIsPending(true);

    fetch(`http://localhost:8000/tasks/${id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to delete task");
        }
        return response.json();
      })
      .then(() => {
        console.log("Task deleted");
        setIsPending(false);
      })
      .catch((error) => {
        console.error("Error deleting task:", error);
        setIsPending(false);
      });
  }


  return (
    <ThemeProvider theme={lightTheme}>
      <div className="dispCard">
        <Item
          key={id}
          elevation={5}
          sx={{
            height: 75,
            lineHeight: "50px",
            textAlign: "left",
            color: "smoke",
            fontSize: 20,
            fontFamily: "Arial",
            padding: "10px",
            borderRadius: 2,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <Checkbox
              checked={status}
              onClick={() => handleCheckboxClick()}
              disabled={isPending}
            />
            <span style={{ textDecoration: status ? "line-through" : "none" }}>
              {task}
            </span>
          </div>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Fab color="secondary" aria-label="edit" style={{ marginRight: "10px" }} >
              <EditIcon />
            </Fab>
            <Fab color="error" aria-label="delete" onClick={() => handleDeleteClick()}>
              <DeleteIcon />
            </Fab>
          </div>
        </Item>
      </div>
    </ThemeProvider>
  );
}
