import * as React from "react";
import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import { createTheme, ThemeProvider, styled } from "@mui/material/styles";
import { Checkbox } from "@mui/material";
import Fab from "@mui/material/Fab";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  textAlign: "center",
  color: theme.palette.text.secondary,
  height: 60,
  lineHeight: "60px",
}));

const darkTheme = createTheme({ palette: { mode: "dark" } });
const lightTheme = createTheme({ palette: { mode: "light" } });

export default function Card({ id, task, initialStatus, handleStatusChange }) {
  const [status, setStatus] = React.useState(initialStatus);

  const handleCheckboxClick = () => {
    const updatedStatus = !status;
    setStatus(updatedStatus);

    // Update status in the backend
    fetch(`http://localhost:8000/tasks/${id}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ status: updatedStatus }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log("Task status updated", data);
    })
    .catch(error => {
      console.error("Error updating task status:", error);
    });

    if (handleStatusChange) {
      handleStatusChange(id, updatedStatus);
    }
  };

  return (
    <ThemeProvider theme={lightTheme}>
      <div className="dispCard">
        <Item
          key={id}
          elevation={5}
          sx={{
            height: 75, // Set height to 50px
            lineHeight: "50px", // Set line height to 50px
            textAlign: "left", // Center the text
            color: "smoke", // Set text color to white
            fontSize: 20, // Set font size to 20px
            fontFamily: "Arial", // Set font family to Arial
            padding: "10px", // Set padding to 10px
            borderRadius: 2, // Make it a circle shape
            display: "flex", // Use flexbox for layout
            alignItems: "center", // Align items vertically
            justifyContent: "space-between", // Space items evenly
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <Checkbox checked={!status} onClick={handleCheckboxClick} />
            <span style={{ textDecoration: status ? "none" : "line-through" }}>
              {task}
            </span>
          </div>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Fab color="secondary" aria-label="edit" style={{ marginRight: "10px" }}>
              <EditIcon />
            </Fab>
            <Fab color="error" aria-label="delete">
              <DeleteIcon />
            </Fab>
          </div>
        </Item>
      </div>
    </ThemeProvider>
  );
}
