import * as React from "react";
import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import { createTheme, ThemeProvider, styled } from "@mui/material/styles";
import { Checkbox } from "@mui/material";
import { alignProperty } from "@mui/material/styles/cssUtils";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  textAlign: "center",
  color: theme.palette.text.secondary,
  height: 60,
  lineHeight: "60px",
}));

const darkTheme = createTheme({ palette: { mode: "dark" } });
const lightTheme = createTheme({ palette: { mode: "light" } });

export default function Card() {
return (
    <ThemeProvider theme={lightTheme}>
        <Box
            sx={{
                p: 1,
                borderRadius: 2,
                display: "grid",
                gridTemplateColumns: { md: "1fr" },
                gap: 2,
                alignItems: "center" , // Align items in the middle
            }}
        >
            <Item
                key={1}
                elevation={5}
                sx={{
                    height: 50, // Set height to 50px
                    // width: , // Set width to 30px
                    lineHeight: "50px", // Set line height to 50px
                    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)", // Set white shadow
                    borderRadius: 2, // Make it a circle shape
                }}
            >
                <input type="checkbox" /> Lorem ipsum dolor sit amet
            </Item>
            <Item
                key={2}
                elevation={5}
                sx={{
                    height: 50, // Set height to 50px
                    lineHeight: "50px", // Set line height to 50px
                    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.5)", // Set white shadow
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    borderRadius: 2, // Make it a circle shape
                }}
            >
                <Checkbox /> Lorem ipsum dolor sit amet
            </Item>
        </Box>
    </ThemeProvider>
);
}
